import logo from '../logo.svg';
import '../components/modal.css';
import React from 'react';
import { Link } from 'react-router-dom';
import { withRouter } from '../components/withRouteCpnt.js';
import { getIdeas, getSearchIdea } from '../api.js';
import {ListItemsCompnt} from '../components/ItemsCpnt';

class ViewItems extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      itemList: [],
      cont: null,
    };
  }

  componentDidMount = () => {
    this.getAllIdeas(-1);
  }
  handleClick = (_searchItems) => {
    //console.log('v ', _searchItems);
    this.props.wired(_searchItems);
  }
  modalClose = () => {
    this.setState({showModal: false});
  }
  paramWire = (_param) => {
    //this.setState({category: _param})
    this.getAllIdeas(_param);
  }

  selectBranch = () => {
    return(<>
      <SearchBarCpnt
        onClick={(inputstring) => this.searchButton(inputstring)}
      />
      <this.clicktoSale></this.clicktoSale>
    </>
    )
  }

  getAllIdeas = async(_category) => {
    let type = this.props.router.params.mode;
    if(type === 'idea')
      type = 0;
    else
      type = 1;
    this.setState({ itemList: await getIdeas(type, _category) });
  }

  searchButton = async(_searchString) => {
    this.setState({
      itemList:  await getSearchIdea(_searchString)
    });
  }

  clicktoSale = () => {
    return(
      <>
        <section>
        <button onClick={() => this.setState({showModal: true})} style={{width: '200px', height: 'auto', fontSize: 'inherit'}}>카테고리별 검색</button>
        {this.props.router.params.mode === 'idea' ? 
          <Link to="/create" style={{ textDecoration: 'none' }}><button style={{width: '200px', height: 'auto', backgroundColor: '#3e5ab1', fontSize: 'inherit'}}>아이디어 업로드</button></Link>
          :<Link to="/createmarket" style={{ textDecoration: 'none' }}><button style={{width: '200px', height: 'auto', backgroundColor: '#3e5ab1', fontSize: 'inherit'}}>경매 올리기</button></Link>}
        </section>

        { this.state.itemList.length ? <section style={{marginLeft:'139px'}}>
          <p style={{color: '#a5a5a5', textAlign: 'left', margin: 20, fontSize: "15px"}}>총 {this.state.itemList.length}건 <br/>
          아이디어의 세부 정보를 확인하려면 해당 아이디어를 클릭하세요</p>
          {this.state.itemList.map(searchItems => (
            <ListItemsCompnt
              key={searchItems.title}
              title = {searchItems.title}
              description = {searchItems.description}
              teamid = {searchItems.id}
              type = {searchItems.type}
              genre = {searchItems.jenre}
              onClick={() => this.handleClick(searchItems)}
            />
          ))}
          </section> : <p style={{margin: 5, fontSize: "15px"}}>검색 결과가 없습니다.</p>
        }
        <CategoryModal
          showFlag={this.state.showModal} onClose={()=>{this.modalClose()}} paramWire={(params)=>{this.paramWire(params)}}
        />
      </>
    )
  }

  render(){
    return(
      <section className="App-header">
        <this.selectBranch></this.selectBranch>
      </section>
    )
  }
}
class SearchBarCpnt extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      searchString: null,
      account: this.props.account,
    };
  }
  changeInput = (_evt) => {
    this.setState({searchString: _evt.target.value});
  }
  render(){
    return(
      <>
        <div className="search_bar" style={{alignSelf:'center'}}>
            <i className="fas fa-search search_icon"></i>
            <input id="text2" type="text" onChange={this.changeInput} placeholder="키워드 입력"></input>
            <button className="fas fa-times search_icon" onClick={() => this.props.onClick(this.state.searchString)}>s</button>
        </div>
      </>
    )
  }
}
export const CategoryModal = ({showFlag, onClose, paramWire}) => {
  const checked = (evt) => {
    const category = evt.target.value;
    switch(category){
      case '0':
        paramWire(0);
        onClose();
        break;
      case '1':
        paramWire(1);
        onClose();
        break;
      case '2':
        paramWire(2);
        onClose();
        break;
      case '3':
        paramWire(3);
        onClose();
        break;
      case '-1':
        paramWire(-1);
        onClose();
        break;
    }
  }
  return(
    <>{showFlag ? ( // showFlagがtrueだったらModalを表示する
    <div id="overlay" className='overlay'>
      <div id="modalcontents" className="modalcontents">
        <div>
          <p>분류</p>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='-1' onChange={checked}></input>
          전체보기</label>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='0' onChange={checked}></input>
          공학/과학</label>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='1' onChange={checked}></input>
          사회/문화</label>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='2' onChange={checked}></input>
          음악/미술</label>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='3' onChange={checked}></input>
          기타분야</label>
        </div>
      </div>
    </div>
    ) : (
      <></>// showFlagがfalseの場合はModalは表示しない)
    )}
    </>
  )
}
export default withRouter(ViewItems);
