import React, { useEffect, useState } from "react";
import { postHold, getPlayersId, putBidIdea } from "../api.js";
import { registChecker } from "./registCatch.js";

export const AuctionInput = ({showFlag, account, onClose, cont, ptcps}) => {

  const [recognize, setRecog] = useState(false);

  const getNow = () => {
    const genzai = Date.now();
    console.log(genzai);
  }
  const recogBtn = () => {
    setRecog(true);
  }
  return(
    <>{showFlag ? ( // showFlagがtrueだったらModalを表示する
    <div id="overlay" className='overlay'>
      <div id="modalcontents" className="modalcontents">
        <div>
          { recognize ? <>
            <p>입찰</p>
            <InputForm useraddr={account} contents={cont} ptcps={ptcps}/>
            <button onClick={onClose}>close</button>
          </>:<>
            <ol>
              <li>가장 높은 호가를 부른 참여자는 입찰기간 종료후 호가만큼의 포인트를 에스크로에 이체시켜야 합니다.</li>
              <li>이체가 완료되면 해당 아이디어의 구현, 원리등 자세한 내용이 담긴 상세파일을 열람할 수 있습니다.</li>
              <li>상세파일의 열람사실은 분산원장에 영구히 기록되며 파일 내용의 무단배포/도용등의 분쟁발생시 해결을 위한 기록으로 사용될 수 있습니다.</li>
              <li>상세파일에 기재된 내용이 부실하다고 생각될 경우 거래를 포기할 수 있습니다.</li>
            </ol>
          <button onClick={recogBtn}>위 사항을 숙지하였습니다.</button>
          </>}

        </div>
      </div>
    </div>
    ) : (
      <></>// showFlagがfalseの場合はModalは表示しない)
    )}
    </>
  )
}

function InputForm({useraddr, contents, ptcps}) {
  const [price , setPrice] = useState(contents.ideaToken);
  const [playerId , setId] = useState();
  const [call , setCall] = useState();
  const name = contents.title;

  useEffect(()=>{
    regiCheck();
  },[]);

  const regiCheck = () => {
    setId(registChecker(useraddr));
  }

  const sendBtn = () => {
    if(call >= price){
      ptcpInAuction();
      alert('입찰 되었습니다');
    }
    else{
      alert('입찰할 토큰 수량은 ' + price + '보다 커야합니다.');
    }
  }
  const ptcpInAuction = () => {
    let firstPtcp = true;
    if(ptcps > 0)
      firstPtcp = false;

    const result = putBidIdea({
      teamId: contents.id, 
      userId: useraddr,
      bidtoken: call,
      first: firstPtcp
    });
    console.log(result);
  }

  return(
    <>
      아이디어 이름: {name} <br/>
      <p style={{margin: 0, fontSize: "18px"}}>현재 호가 또는 시작가: {price}</p>
      <p style={{margin: 0, fontSize: "18px"}}>경매 참여자 수: {ptcps}</p>
      <br/>입찰할 포인트:<br/>
      <input type='number' min={price} max={999999} step={1} onChange={(evt)=>setCall(evt.target.value)}></input>
      <br/><br/>
      <button className="App-exeButton2" onClick={sendBtn}>send</button>
    </>
  )
}
