import React, { useEffect, useState } from "react";
import { postUserRegi, putLogin } from "../api";

export const LoginInput = () => {

  const [inputId, setId] = useState();
  const [inputPass, setPass] = useState();

  const submit = () => {
    const record = {
      playerid: inputId,
      userpass: inputPass
    };
    console.log(record);
    //const result = await putLogin(record);
  }
  const getId = (_evt) => {
    setId(_evt.target.elements.id.value);
  }
  const getPass = (_evt) => {
    setPass(_evt.target.elements.pw.value)
  }
  return(
    <section className="App-header" style={{alignItems: 'center'}}>
      login <br/>
      <input onChange={getId} className='input-login' name="id" placeholder='id'/>
      <input onChange={getPass} className='input-login' name="pw" placeholder='password'/><br/>
      <button onClick={submit} className="App-exeButton2">login</button>
      <br/><br/>
      <a className="App-link" href="http://localhost:3000/useregist">아이디가 없으신가요?</a>
    </section>
  )
}

export const RegistInput = () => {
  async function loginSubmit(evt) {
    const record = {
      playerid: evt.target.elements.id.value,
      userpass: evt.target.elements.pw.value,
      username: evt.target.elements.name.value
    };
    await postUserRegi(record);
  }
  return(
    <section className="App-header">
      <form onSubmit={loginSubmit}>
        <input className='input-login' name="id" placeholder='id'/><br/>
        <input className='input-login' name="pw" placeholder='password'/><br/>
        <input className='input-login' name="name" placeholder='name'/><br/>
        <button type="submit" onClick={() => alert('계정 생성 완료')} className="App-exeButton2">submit</button>
      </form>
    </section>
  )
}
