import logo from '../logo.svg';
import '../App.css';
import React from "react";
import { Link } from 'react-router-dom';

export class Home extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      user: this.props.user,
    };
    //this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  koujicyuu = () => {
    alert('공사중');
  }

  render(){
    return(
      <div className="App">
        <section >
          <p style={{textAlign: 'left', margin:'0px'}}>{sessionStorage.getItem('userid')}</p>
        </section>
        <section className="App-header">
          <nav className='hori'>
            <Link to="/login" style={{ textDecoration: 'none' }}>
              <button className='button04' style={{width:'175px', borderColor:'darkgray', color:'ivory'}}>로그인</button>
            </Link>
            <Link to="/search/idea" style={{ textDecoration: 'none' }}>
              <button className='button04' style={{width:'300px'}}>광장</button>
            </Link>
          </nav> 
          <nav className='hori'>
            <Link to="/search/market" style={{ textDecoration: 'none' }}>
              <button className='button04'>시장</button>
            </Link>
            <Link to="/search/market" style={{ textDecoration: 'none' }}>
              <button className='button04'>협력</button>
            </Link>
          </nav>
          <section>
          <p style={{fontSize: "15px"}}>아이디어 광장: 아이디어를 자유롭게 공유하고 열람할 수 있습니다.</p>
            <p style={{fontSize: "15px"}}>아이디어 시장: 경매를 통해 아이디어를 사고팔수 있습니다.</p>
          </section>
      </section>
    </div>
    )
  }
}
