import React from "react";
import { useState, useEffect } from "react";
import FileViewer from "react-file-viewer";
import { getIdeaOne } from "../api";

export const ViewFile = (ideaParams) => {
  const [checkFild, setCheck] = useState(false);
  const [view, setView] = useState(false);
  //const file = `https://ideaideaserver.herokuapp.com/viewdocx/${ideaParams.title}`;
  const file = `http://giparang.as:3039/viewdocx/${ideaParams.title}`;
  const type = "docx";

  useEffect(()=>{
    fileinspect();
  },[]);

  const fileinspect = async() => {
    const fileCheck = await getIdeaOne(ideaParams.teamid);
    console.log(fileCheck.file);
    setCheck(fileCheck.file);
  }
  const handleView = () => {
    if(checkFild)
      setView(!view);
    else
      alert('file does not exist');
  };

  return (
    <div style={{color:'black'}}>
      <button onClick={handleView} className="button02">이 아이디어의 상세설명 펼치기</button>
      {view && (
        <FileViewer
          fileType={type}
          filePath={file}
        />
      )}
    </div>
  );
}
