import React from 'react';
import { Link } from "react-router-dom";
import { 
  getPlayers, 
  getIdeaOne, 
  putUpdateIdea,
  putUpdateHold,
  dltHold, 
  getTeamPlayers,
  putPrice} from "../api.js";
import './modal.css';

// 子コンポーネント（モーダル）
export class JoinModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editor: null,
      title: null,
    };
  }

  modalDown = () => this.props.onClick();

  alertStatus = () => {
    const mode = this.props.content.status;
    console.log(mode);
    if(mode === 0){
      return(
        <Type_zero
          content={this.props.content}
          title={this.state.title}
          onClick={this.props.onClick}
        />
      )
    }
    else if(mode === 1){
      return(
        <Type_excute
          content={this.props.content}
          title={this.state.title}
          account={this.props.account}
          contract={this.props.contract}
          onClick={this.props.onClick}
        />
      )
    }
    else if(mode === 2){
      return(
        <Type_reject
          content={this.props.content}
          title={this.state.title}
          onClick={this.props.onClick}
        />
      )
    }
    else if(mode === 3){
      return(
        <Type_purchase
          content={this.props.content}
          title={this.state.title}
          onClick={this.props.onClick}
      />)
    }
    else if(mode === 4){
      return(
        <Type_purchaseXE
          content={this.props.content}
          title={this.state.title}
          account={this.props.account}
          contract={this.props.contract}
          onClick={this.props.onClick}
      />)
    }
    else if(mode ===5){
      return(
        <Type_bid
        content={this.props.content}
        onClick={this.props.onClick}
      />)
    }
    else{
      console.log('mode err')
    }
  }

  render(){
    return(
      <>
        {this.props.showFlag ? ( // showFlagがtrueだったらModalを表示する
        <div id="overlay" className='overlay'>
          <div id="modalcontents" className="modalcontents" onClick={(event)=>{event.stopPropagation()}}>
            <this.alertStatus></this.alertStatus>
          </div>
        </div>
        ) : (
          <></>// showFlagがfalseの場合はModalは表示しない)
        )}
      </>
    );
  }
}

class Type_zero extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      title: null,
      content: this.props.content,
      account: this.props.account,
    };
  }

  componentDidMount = async() => {
    this.setState({title: await getDocuTitle(this.state.content.team_id)});
  }
  dltIdea = async() => {
    await dltHold(this.state.content.id);
  }
  modalDown = () => this.props.onClick();

  render(){
    return(
      <>
        <p>・후원알림</p>
        <section style={{fontSize: "20px", lineHeight: "10px"}}>
          <p>문서제목: {this.state.title}</p>
          <p>후원토큰: {this.props.content.tokn}</p>
          <p>후원자: uid-{this.state.content.user_id}</p>
        </section>
        <button onClick={this.props.onClick}>close</button>
        <button onClick={() => {
          this.dltIdea();
          this.modalDown();
        }}>알림삭제</button>
        <Link to={'/ideadetails/' + this.state.content.team_id + '/0'} style={{ textDecoration: 'none' }}>
        <button>문서로 이동</button></Link>
      </>
    )
  }
}

class Type_excute extends React.Component{
  constructor(props) {
    super(props);
