import logo from '../logo.svg';
import '../App.css';
import React from "react";
import { Link } from 'react-router-dom';

export class Home extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      uid: sessionStorage.getItem('userid'),
      logined: false
    };
    //this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  componentDidMount = () => {
    if(this.state.uid !== null){
      this.setState({logined: true});
    }

  }
  koujicyuu = () => {
    alert('공사중');
  }

  storageClear = () => {
    sessionStorage.clear();
    window.location.reload();
  }

  render(){
    return(
      <div className="App">
        <section className="App-header">
          <nav className='hori'>
            {this.state.logined ? <button className='button04' onClick={this.storageClear} style={{padding:'1em', width:'168px', borderColor:'#524951'}}>로그아웃</button>
              :<Link to="/login" style={{ textDecoration: 'none' }}>
                <button className='button04' style={{width:'168px', borderColor:'#524951'}}>로그인</button>
              </Link>
            }
            <Link to="/myinfo" style={{ textDecoration: 'none' }}>
              <button className='button04' style={{width:'300px'}}>마이페이지</button>
            </Link>
          </nav>
          <section>
            <p style={{fontSize: "15px"}}>로그인: 아이디어를 공유/판매 하려면 로그인이 필요합니다.</p>
            <p style={{fontSize: "15px"}}>마이페이지: 내가 올린 아이디어나 알림을 확인할 수 있습니다.</p>
            <p style={{fontSize: "15px"}}>포인트센터(가칭): 미정.</p>
          </section>
          <nav className='hori'>
          <button style={{width:'168px', borderColor:'#524951'}}></button>
            <Link to="/search/idea" style={{ textDecoration: 'none' }}>
              <button className='button04' style={{width:'300px'}}>광장</button>
            </Link>
          </nav> 
          <nav className='hori'>
            <Link to="/search/market" style={{ textDecoration: 'none' }}>
              <button className='button04'>시장</button>
            </Link>
            {/* <Link to="/search/market" style={{ textDecoration: 'none' }}> */}
              <button onClick={this.koujicyuu} className='button04'>협력</button>
            {/* </Link> */}
          </nav>
          <section>
            <p style={{fontSize: "15px"}}>광장: 아이디어를 자유롭게 공유하고 열람할 수 있습니다.</p>
            <p style={{fontSize: "15px"}}>시장: 아이디어 경매를 통해 아이디어를 사고팔수 있습니다.</p>
            <p style={{fontSize: "15px"}}>협력: 아이디어를 타인과 협력하여 완성해나갈 수 있습니다.</p>
          </section>
      </section>
    </div>
    )
  }
}
