import React from 'react';
import { AuctionInput } from '../components/auctionInput.js';
import { withRouter } from '../components/withRouteCpnt.js';
import { ViewFile } from '../components/fileViewer.js';
import { StatusGuide } from '../components/statusGuide.js';
import { 
    getIdeaOwner, getPlayers, putAddGood,
    getIdeaOne, getRemainTime, postTeamReply, getReply, getPushCheck } from '../api';

class IdeaDetails extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      slide: true,
      remain: null,
      uid: sessionStorage.getItem('userid'),
      owner: null,
      showFlag: false,
      ptcpsNum: 0,
      contents: null,

    };
  }
  componentWillUnmount = () => {
    clearTimeout(this.timer);
  }
  componentDidMount = () => {
  //console.log(this.props.router);
  this.getContents();

    setTimeout(() => {
      this.setState({slide: false});
    }, 1300);
  }
  getContents = () => {
    const teamId = parseInt(this.props.router.params.teamid, 10);
    getIdeaOne(teamId).then(content => {
      this.setState({contents: content});
      this.getPlayer(content.id);
    })
  }
  getPlayer = (_ideaid) => {
    getIdeaOwner(_ideaid).then((data) => {
      this.setState({owner: data});
    });
    getPlayers(_ideaid).then((data => {
      this.setState({ptcpsNum: data.length});
    }));
  }

  viewBranch = () => {
    const ideaType = parseInt(this.props.router.params.type, 10);
    if(ideaType == 0){
      return(
        <div className='textprerap'>
          <GoodAndPush
            content={this.state.contents}
            account={this.state.uid}
            writer={this.state.owner}
          />
          <InputReply
            account={this.state.uid}
            teamId={this.props.router.params.teamid}
          />
        </div>
      )
    }
    else if(ideaType == 1){
      return(
        <div className='textprerap'>
          <InfoBlock
            contents={this.state.contents}
            account={this.state.uid}
            ptcpNum={this.state.ptcpsNum}
            openAuctionIntput={()=>this.setState({showFlag: true})}
          />
          <AuctionInput 
            showFlag={this.state.showFlag} account={this.state.uid} 
            cont={this.state.contents} ptcps={this.state.ptcpsNum}
            onClose={()=>{this.modalClose()}}/>
        </div>
      )
    }
    else{
      return(
      <div className='textprerap'>
        <ul>
          <li>ÏïÑÏù¥ÎîîÏñ¥Ïùò Ïã§Ìñâ/Íµ¨ÌòÑÏóê Í¥ÄÌïú ÏÑ§Î™Ö</li><br/>
          <ViewFile
              title={this.state.contents.title+'d'}
              teamid={this.state.contents.id}
              confidence={true}
            />
          <p>
          ÎÇôÏ∞∞ÏùÑ ÌôïÏ†ïÏßìÏßÄ ÏïÑÎãàÌïú ÏÉÅÌÉúÏóêÏÑú ÏúÑ ÌååÏùºÏùò ÎÇ¥Ïö©ÏùÑ ÎèÑÏö©ÌïòÍ±∞ÎÇò ÌÉÄÏù∏ÏóêÍ≤å Î∞∞Ìè¨Î•º Í∏àÌï©ÎãàÎã§.<br/>
          Ìï¥Îãπ ÌååÏùºÏùò Ïó¥ÎûåÏÇ¨Ïã§ÏùÄ Î∂ÑÏÇ∞ÏõêÏû•Ïóê ÏòÅÍµ¨Ìûà Í∏∞Î°ùÎêòÎ©∞ Ïù¥ Í∏∞Î°ùÏùÄ ÌååÏùºÏùò Î¨¥Îã®ÎèÑÏö©/Î∞∞Ìè¨Ïãú Î∂ÑÏüÅÌï¥Í≤∞Ïóê ÏÇ¨Ïö©Îê† Ïàò ÏûàÏäµÎãàÎã§.</p>
        </ul>
      </div>
      )
    }

  }
  modalClose = () => {
    this.setState({showFlag: false});
    document.removeEventListener('click',this.modalClose);
  }
  // modalOpen = () => {
  //   this.setState({showFlag: true});
  // }

  render(){
    return(
      <div><p style={{textAlign: 'left', margin:'0px'}}>{this.state.uid}</p>
      <section className="App-header">
        {this.state.slide ? <p>ÏµúÍ≥†Ïùò ÏïÑÏù¥ÎîîÏñ¥ Í≥µÏú† ÌîåÎû´Ìèº /Threads <br/>Î¨∏ÏÑú Î°úÎìú Ï§ë...</p>
        :<>
          <ul className='textprerap'>
            <li>Ï†úÎ™©</li>
            <p>{this.state.contents.title}</p>
            <li>ÏïÑÏù¥ÎîîÏñ¥ ÏÑ§Î™Ö</li><p style={{width:'599px'}}>{this.state.contents.description}</p>
            <ViewFile
              title={this.state.contents.title}
              teamid={this.state.contents.id}
              confidence={false}
            />
          </ul>
          <this.viewBranch></this.viewBranch>
        </>
        }
      </section></div>
    )
  }
}
class GoodAndPush extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      showFlag: false,
      content: this.props.content,
      writer: this.props.writer,
      account: this.props.account,
    };
  }
  goodBtn = async() => {
    const record = {
      owner: this.state.writer,
      teamId: this.state.content.id,
      userId: this.state.account,
    }
    await putAddGood(record).then((data) => {
      if(data)
        alert('Ïù¥ÎØ∏ Ï¢ãÏïÑÏöîÎ•º ÎàÑÎ•¥ÏÖ®ÏäµÎãàÎã§');
      else
        alert('Ï∂îÏ≤úÏù¥ Î∞òÏòÅÎêòÏóàÏäµÎãàÎã§');
    });
  }
  render(){
    return(<section className='fixposition'>
      <p style={{color: 'black', marginLeft: '1em', marginBottom: '0px'}}>Ï∂îÏ≤úÏàò: {this.state.content.good}</p>
      <button onClick={this.goodBtn}><span role="img" aria-label="good">üëç</span>Ï¢ãÏïÑÏöî</button>
    </section>)
  }
}
class InputReply extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      replyWirter: [],
      replyContent: [],
      content: null,
      teamId: this.props.teamId,
      account: this.props.account,
    };
  }
  componentDidMount = async() => {
    const reply = await getReply(this.state.teamId);
    let len = reply.length;
    let content = [];
    let writer = [];

    while(len){
      content.push(reply[len-1].description);
      writer.push(reply[len-1].account);
      len-=1;
    }
    //console.log(content);
    this.setState({replyContent: content});
    this.setState({replyWirter: writer});
  }
  upReButton = () => {
    const record = {
      content: this.state.content,
      location: this.state.teamId,
      account: this.state.account,
    }
    postTeamReply(record).then(result => {
      if(result){
        let copyReplys = [...this.state.replyContent];
        let copyReplyWriters = [...this.state.replyWirter];
        copyReplys.push(this.state.content);
        copyReplyWriters.push(this.state.account);
        this.setState({replyContent: copyReplys});
        this.setState({replyWirter: copyReplyWriters});
      }
      else
        alert('err 83201');
    })
  }
  onReplChange = (evt) => {
    this.setState({content: evt.target.value});
  }
  render(){
    return(<>
      <p>ÎåìÍ∏Ä Î™©Î°ù.</p>
      <ul>
        {this.state.replyContent.map((replys, index) => (
          <li key={index} style={{margin:'24px'}}>
            <p style={{fontSize:'17px', margin:'auto', color:'#999'}}>{this.state.replyWirter[index]}ÎãòÏùò ÎåìÍ∏Ä</p>
            {this.state.replyContent[index]}
          </li>
        ))}
      </ul>
      <textarea name="docudesc" rows='3' cols='60' placeholder="ÎåìÍ∏Ä ÏûÖÎ†•" onChange={this.onReplChange}/>
      <button onClick={this.upReButton}>ÎåìÍ∏ÄÎã¨Í∏∞</button>
    </>)
  }
}

class InfoBlock extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      remainTime: {days: 0, hours: 0, minutes: 0, seconds: 0},
      showFlag: false,
      ideaStatus: null,
      ptcpsNum: this.props.ptcpNum,
      contents: this.props.contents
    };
  }

  componentWillUnmount = () => {
    clearInterval(this.timer);
  }
  componentDidMount = () => {
    if(this.state.contents.days!==null){
      this.timer = setInterval(() => {
        getRemainTime(this.state.contents.id).then(result => {
          this.setState({remainTime: result});
        })
      }, 1000);
    }
  }

  render(){
    return(<>
      <ul>
        <li style={{margin: 'inherit'}}>ÏïÑÏù¥ÎîîÏñ¥ Í≤ΩÎß§Ï†ïÎ≥¥</li>
        <p style={{margin: 0}}>Í≤ΩÎß§ Ï∞∏Ïó¨Ïûê Ïàò: {this.state.ptcpsNum}</p>
        <p style={{margin: 0}}>ÌòÑÏû¨ Ìò∏Í∞Ä ÎòêÎäî ÏãúÏûëÍ∞Ä: {this.state.contents.ideaToken} points</p>
        {this.state.ptcpsNum>0 ? <p style={{margin: 0}}> ÎÇ®ÏùÄ Í≤ΩÎß§ ÏãúÍ∞Ñ: {this.state.remainTime.days}Ïùº {this.state.remainTime.hours}ÏãúÍ∞Ñ {this.state.remainTime.minutes}Î∂Ñ {this.state.remainTime.seconds}Ï¥à</p>:<></>}
        <button onClick={this.props.openAuctionIntput} className="button02">Ïù¥ ÏïÑÏù¥ÎîîÏñ¥Ïùò Í≤ΩÎß§Ïóê Ï∞∏Ïó¨ÌïòÍ∏∞</button>
      </ul>
    </>)
  }
}

const docuLink = (_title) => {
  let downlink = 'http://giparang.asuscomm.com:3039/downloadfile/';
  //let downlink = 'localhost:3039/downloadfile/';
  const result = window.confirm('ÌååÏùºÏù¥ Îã§Ïö¥Î°úÎìú Îê©ÎãàÎã§');

  if(result){
    downlink += _title;
    return([true, downlink]);
  }
  else{
    alert('Îã§Ïö¥Î°úÎìúÍ∞Ä Ï∑®ÏÜåÎê®.');
    return([false, '']);
  }
}
export default withRouter(IdeaDetails);
