import logo from '../logo.svg';
import '../App.css';
import '../components/animate.css';
import React from "react";
import { Link } from 'react-router-dom';
import { getAllPoints } from '../api';

export class Home extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      aniSelector: 0,
      mintedPoint: 0
    };
    //this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  componentDidMount = () => {
    if(this.state.uid !== null){
      this.setState({logined: true});
    }
    getAllPoints().then(point => {
      this.setState({mintedPoint: point});
    })
  }
  koujicyuu = () => {
    alert('공사중');
  }

  storageClear = () => {
    sessionStorage.clear();
    window.location.reload();
  }

  animationA = () => {
    if(this.state.aniSelector === 0){
      return(
        <section style={{fontSize: '17px', margin: '70px'}}>
            <p>광장: 아이디어나 관련질의를 공유하고 열람할 수 있습니다.</p>
            <p>시장: 아이디어 경매를 통해 아이디어를 사고팔수 있습니다.</p>
            <p>협력: 아이디어를 타인과 협력하여 완성해나갈 수 있습니다.</p>
          </section>
      )
    }
    else if(this.state.aniSelector===1){
      return(
        <>
          <span className="scrollDown" style={{alignSelf: 'center'}}></span>
            <span className="circle"></span>
            <div className='hori'>
            <span className="scrollDown" style={{transform:'rotate(225deg)', margin:'65px', marginTop:'0px'}}></span>
            <span className="scrollDown" style={{transform:'rotate(135deg)', margin:'65px', marginTop:'0px'}}></span>
            </div>
        </>
      )
    }
    else if(this.state.aniSelector===2){
      return(
        <>
        <span className="scrollDown" style={{alignSelf: 'center'}}></span>
          <span className="flikerCircle"></span>
          <div className='hori'>
          <span className="scrollDown" style={{transform:'rotate(225deg)', margin:'65px', marginTop:'0px'}}></span>
          <span className="fliker" style={{transform:'rotate(135deg)', margin:'65px', marginTop:'0px'}}></span>
          </div></>
      )
    }
  }

  render(){
    return(
      <div className="App">
        <section className="App-header">
          <section>
            <br/>
          </section>
          <Link to="/myinfo" style={{ alignSelf:"center", textDecoration: 'none' }}>
          <button className='button04' style={{ width:"430px", height:'130px', textAlignLast:'left'}}>웹이 보유한 총 이더량: 100 eth <br/>유통중인 총 포인트량: {this.state.mintedPoint}p</button>
          </Link>
          <this.animationA />
          <nav className='hori'>
            {/* <button className='space'></button> */}
            <Link to="/search/idea" style={{ textDecoration: 'none' }}>
              <button className='button04' onMouseOver={() => this.setState({aniSelector: 1})}>광장</button>
            </Link>
            <Link to="/search/market" style={{ textDecoration: 'none' }}>
              <button className='button04'onMouseOver={() => this.setState({aniSelector: 2})}>시장</button>
            </Link>
            {/* <Link to="/search/market" style={{ textDecoration: 'none' }}> */}
              <button onClick={this.koujicyuu} className='button04' onMouseOver={() => this.setState({aniSelector: 3})}>협력</button>
            {/* </Link> */}
          </nav>


      </section>
    </div>
    )
  }
}
