import React from 'react';
import { AuctionInput } from '../components/auctionInput.js';
import { withRouter } from '../components/withRouteCpnt.js';
import { ViewFile } from '../components/fileViewer.js';
import { PushInput } from '../components/apushInput.js';
import { RetreatPush } from '../components/apushInput.js';
import { StatusGuide } from '../components/statusGuide.js';
import { 
    getIdeaOwner, getPlayers, putAddGood,
    getIdeaOne, getRemainTime, postTeamReply, getReply, getIdeaPoints, getPushCheck } from '../api';

class IdeaDetails extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      slide: true,
      remain: null,
      uid: sessionStorage.getItem('userid'),
      owner: null,
      showFlag: false,
      ptcpsNum: 0,
      pushed: 0,
      contents: null,

    };
  }
  componentWillUnmount = () => {
    clearTimeout(this.timer);
  }
  componentDidMount = () => {
  //console.log(this.props.router);
  this.getContents();

    setTimeout(() => {
      this.setState({slide: false});
    }, 1300);
  }
  getContents = () => {
    const teamId = parseInt(this.props.router.params.teamid, 10);
    getIdeaOne(teamId).then(content => {
      this.setState({contents: content});
      this.getPlayer(content.id);

    })
    getPushCheck(this.state.uid, teamId).then(result => {
      this.setState({pushed: result});
    })
  }
  getPlayer = (_ideaid) => {
    getIdeaOwner(_ideaid).then((data) => {
      this.setState({owner: data});
    });
    getPlayers(_ideaid).then((data => {
      this.setState({ptcpsNum: data.length});
    }));
  }


  viewBranch = () => {
    const ideaType = parseInt(this.props.router.params.type, 10);
    if(ideaType == 0){
      return(
        <div className='textprerap'>
          <GoodAndPush
            content={this.state.contents}
            account={this.state.uid}
            writer={this.state.owner}
            recant={this.state.pushed}
          />
          <InfoBlock
            contents={this.state.contents}
            account={this.state.uid}
            ptcpNum={this.state.ptcpsNum}
          />
          <InputReply
            account={this.state.uid}
            teamId={this.props.router.params.teamid}
          />
        </div>
      )
    }
    else if(ideaType == 1){
      return(
        <div className='textprerap'>
          <ul>
            <li style={{margin: 'inherit'}}>아이디어 경매정보</li>
            <p style={{margin: 0}}>경매 참여자 수: {this.state.editors.length - 1}</p>
            <p style={{margin: 0}}>현재 호가 또는 시작가: {this.state.contents.ideaToken} token</p>
            <p style={{margin: 0}}> 남은 경매 시간: {this.state.remain}</p>
            <button onClick={this.autionButon} className="button02">이 아이디어의 경매에 참여하기</button>
          </ul>
          <AuctionInput 
            showFlag={this.state.showFlag} account={this.state.account} 
            cont={this.state.contents} ptcps={this.state.editors}
            onClose={()=>{this.modalClose()}}/>
        </div>
      )
    }
    else{
      return(
      <div className='textprerap'>
        <ul>
          <li>아이디어 인수</li>
          <ol>
            <li>당신은 이 아이디어의 인수권자입니다.</li>
            <li>상세파일열람과 관련파일 요청을 하기 위해서 우선 에스크로에 낙찰가만큼의 토큰을 이체시켜야 합니다.</li>
            <li>당신은 상세설명파일에 기재된 내용이 인수에 부족하다고 생각할 경우 추가정보를 요청하거나 거래를 포기할 수 있습니다.</li>
            <li>당신이 열람하는 모든 파일은 열람사실이 블록체인상에 기록됩니다.</li>
            <li>아이디어 인수를 포기할 시 에스크로에 이체한 토큰의 80%만 반환합니다.</li>
          </ol>
          <button className="button03">이 아이디어의 상세파일 보기</button>
        </ul>
      </div>
      )
    }

  }
  modalClose = () => {
    this.setState({showFlag: false});
    document.removeEventListener('click',this.modalClose);
  }

  render(){
    return(
      <div><p style={{textAlign: 'left', margin:'0px'}}>{this.state.uid}</p>
      <section className="App-header">
        {this.state.slide ? <p>최고의 아이디어 공유 플랫폼 /Threads <br/>문서 로드 중...</p>
        :<>
          {/* <this.leadPtct></this.leadPtct> */}
          <ul className='textprerap'>
            <li>제목</li>
            <p>{this.state.contents.title}</p>
            <li>아이디어 설명</li><p>{this.state.contents.description}</p>
            <ViewFile
            title={this.state.contents.title}
            teamid={this.state.contents.id}
            />
          </ul>
          <this.viewBranch></this.viewBranch>

        </>
        }
      </section></div>
    )
  }
}
class GoodAndPush extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      showFlag: false,
      recant: this.props.recant,
      content: this.props.content,
      writer: this.props.writer,
      account: this.props.account,
    };
  }
  goodBtn = async() => {
    const record = {
      owner: this.state.writer,
      teamId: this.state.content.id,
      userId: this.state.account,
    }
    await putAddGood(record).then((data) => {
      if(data)
        alert('이미 좋아요를 누르셨습니다');
      else
        alert('추천이 반영되었습니다');
    });
  }
  modalClose = () => {
    this.setState({showFlag: false});
    document.removeEventListener('click',this.modalClose);
  }
  render(){
    return(<section className='fixposition'>
      <p style={{color: 'black', marginLeft: '1em', marginBottom: '0px'}}>추천수: {this.state.content.good}</p>
      <button onClick={this.goodBtn}><span role="img" aria-label="good">👍</span>좋아요</button>
    </section>)
  }
}
class InputReply extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      replyWirter: [],
      replyContent: [],
      content: null,
      teamId: this.props.teamId,
      account: this.props.account,
    };
  }
  componentDidMount = async() => {
    const reply = await getReply(this.state.teamId);
    let len = reply.length;
    let content = [];
    let writer = [];

    while(len){
      content.push(reply[len-1].description);
      writer.push(reply[len-1].account);
      len-=1;
    }
    //console.log(content);
    this.setState({replyContent: content});
    this.setState({replyWirter: writer});
  }
  upReButton = () => {
    const record = {
      content: this.state.content,
      location: this.state.teamId,
      account: this.state.account,
    }
    postTeamReply(record);
    //window.location.reload();
  }
  onReplChange = (evt) => {
    this.setState({content: evt.target.value});
  }
  render(){
    return(<>
      <p>댓글 목록.</p>
      <ul>
        {this.state.replyContent.map((replys, index) => (
          <li key={index} style={{margin:'24px'}}>
            <p style={{fontSize:'17px', margin:'auto', color:'#999'}}>{this.state.replyWirter[index]}님의 댓글</p>
            {this.state.replyContent[index]}
          </li>
        ))}
      </ul>
      <textarea name="docudesc" rows='3' cols='60' placeholder="댓글 입력" onChange={this.onReplChange}/>
      <button onClick={this.upReButton}>reply</button>
    </>)
  }
}

class InfoBlock extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      remainTime: {days: 0, hours: 0, minutes: 0, seconds: 0},
      showFlag: false,
      ideaStatus: null,
      ptcpsNum: this.props.ptcpNum,
      contents: this.props.contents
    };
  }
  componentWillUnmount = () => {
    clearInterval(this.timer);
  }
  componentDidMount = () => {
    if(this.state.contents.ideaToken > 0){
      this.getStatus(this.state.contents.progress);
      if(this.state.contents.progress === 1){
        this.timer = setInterval(() => {
          getRemainTime(this.state.contents.id).then(result => {
            this.setState({remainTime: result});
          })
        }, 1000);
      }
    }
  }
  getStatus = (_status) => {
    switch(_status){
      case 0:
        this.setState({ideaStatus: 'open'});
        break;
      case 1:
        this.setState({ideaStatus: 'excute'});
        break;
      case 2:
        this.setState({ideaStatus: 'vote'});
        break;
    }
  }
  setInfo = () => {
    if(this.state.contents.progress === 0){
      return(<>
        <p style={{margin: 0}}>설정 프로젝트 기간: {this.state.contents.days} 일</p>
      </>)
    }
    else if(this.state.contents.progress === 1){
      return(<>
        <p style={{margin: 0}}>남은 프로젝트 기간: {this.state.remainTime.days}일 {this.state.remainTime.hours}시간 {this.state.remainTime.minutes}분 {this.state.remainTime.seconds}초</p>
      </>)
    }
  }
  statusBtn = () => {
    this.setState({showFlag: true});
  }
  modalClose = () => {
    this.setState({showFlag: false});
    document.removeEventListener('click',this.modalClose);
  }
  render(){
    return(<>
      {this.state.contents.ideaToken > 0 ? <>
        <ul style={{borderBottom: '1px solid rgb(90, 90, 90)'}}>
          <li style={{margin: 'inherit'}}>아이디어 상태정보</li>
          <p style={{margin: 0}}>현재 후원자 수: {this.state.ptcpsNum} 명</p>
          <p style={{margin: 0}}>후원된 포인트: {this.state.contents.holdtoken} points</p>
          <p style={{margin: 0}}>목표 포인트: {this.state.contents.ideaToken} points</p>
          <this.setInfo></this.setInfo>
          <button onClick={this.statusBtn} className="button02">status : {this.state.ideaStatus}</button>
        </ul>
      </> : <>
        <ul>
        <li>이 제안은 프로젝트형 제안이 아닙니다.</li>
        <br/><br/>
        </ul>
      </>}
      <StatusGuide
        showFlag={this.state.showFlag} userAddr={this.props.account} 
        ideaState={this.state.contents.progress}
        teamId={this.state.contents.id}
        onClose={()=>{this.modalClose()}}/>
    </>)
  }
}

const docuLink = (_title) => {
  let downlink = 'http://giparang.asuscomm.com:3039/downloadfile/';
  //let downlink = 'localhost:3039/downloadfile/';
  const result = window.confirm('파일이 다운로드 됩니다');

  if(result){
    downlink += _title;
    return([true, downlink]);
  }
  else{
    alert('다운로드가 취소됨.');
    return([false, '']);
  }
}
export default withRouter(IdeaDetails);
