import React, { useState } from "react";
import { putAdviceIdea } from "../api";

export const CooperInput = ({showFlag, account, onClose, cont, owner}) => {

  const [recognize, setRecog] = useState(false);

  const recogBtn = () => {
    setRecog(true);
  }
  return(
    <>{showFlag ? ( // showFlagがtrueだったらModalを表示する
    <div id="overlay" className='overlay'>
      <div id="modalcontents" className="modalcontents">
        <div>
          { recognize ? <>
            <p>협력</p>
            <InputForm useraddr={account} contents={cont} owner={owner}/>
            <button onClick={onClose}>close</button>
          </>:<>
            <ol>
              <li>업로드한 내용은 모두에게 공개됩니다. </li>
              <li>공개된 내용은 분산원장에 의해 무단배포/도용등의 문제로부터 보호됩니다.</li>
            </ol>
          <button onClick={recogBtn}>위 사항을 이해하였습니다.</button>
          </>}
        </div>
      </div>
    </div>
    ) : (
      <></>// showFlagがfalseの場合はModalは表示しない)
    )}
    </>
  )
}

function InputForm({useraddr, contents, owner}) {
  const [fbolb, setBolb] = useState();
  const [file, setFile] = useState(false);
  const [filename, setName] = useState();
  const [desc, setDesc] = useState('');
  const name = contents.title;

  const onFileInput = (e) => {
    const reader = new FileReader();
    const fileByteArray = [];

    reader.readAsArrayBuffer(e.target.files[0]);
    reader.onloadend = (_evt) => {
      if (_evt.target.readyState === FileReader.DONE) {
        const arrayBuffer = _evt.target.result,
        array = new Uint8Array(arrayBuffer);
        for (const a of array) {
          fileByteArray.push(a);
        }
      console.log(fileByteArray);
      setName(e.target.files[0].name)
      setBolb(fileByteArray);
      setFile(true);
      }
    }
  }
  const sendBtn = () => {
    const record = {
      userAddr: useraddr,
      Desc: desc,
      teamId: contents.id,
      name: filename,
      fbolb: fbolb,
      existFile: file
    }
    putAdviceIdea(record).then(result => {
      console.log(result);
    })
  }
  return(
    <>
      아이디어 이름: {name} <br/><br/>
      조언/제안: <br/>
      <textarea name="docudesc" rows='3' cols='60' onChange={(evt)=>setDesc(evt.target.value)} placeholder="이 아이디어의 조언 또는 제안을 입력"/><br/>
      협력문서(optional)<br/>
      <input type='file' accept="application/pdf" onChange={onFileInput}/>
      <br/><br/>
      <button className="App-exeButton2" onClick={sendBtn}>send</button>
    </>
  )
}
