import React from "react";
import { useState, useEffect } from "react";
import getWeb3 from '../getWeb3';
import EthReceiveContract from '../contracts/ReceiveEther.json';

export const Vender = () => {
    const [ethBal, setEthBal] = useState(0);
    const [contract_wallet ,setWallet] = useState(null);
    const [metaMaskFlag, setMetaMaskFlag] = useState(false);
    const [account, setAccount] = useState(null);
	const [errorMessage, setErrorMessage] = useState(null);

  useEffect(() => {
    connectWeb3();
	const tmpFlag = window.ethereum && window.ethereum.isMetaMask;
	setMetaMaskFlag(tmpFlag);
  },[]);

  const connectWeb3 = async() => {
    try{
        // Get network provider and web3 instance.
        const web3 = await getWeb3();

        // Use web3 to get the user's accounts.
        const accounts = await web3.eth.getAccounts();

        // Get the contract instance.
        const networkId = await web3.eth.net.getId();

        const deployedNetwork = EthReceiveContract.networks[networkId];
        const instance_contract = new web3.eth.Contract(
          EthReceiveContract.abi,
          deployedNetwork && deployedNetwork.address,
        );
        setWallet(instance_contract);
      }
      catch(error){
        alert(
          `Failed to load web3, accounts, or contract. Check console for details.`,
        );
        console.error(error);
      }
  }

  const connectWallet = () => {
    window.ethereum
	.request({ method: "eth_requestAccounts" })
	.then((result) => {
		setAccount(result[0]);
	})
	.catch((error) => {
	  setErrorMessage(error.message);
	});
  }

  const getBalance = async() => {
    const eth_amount = await contract_wallet.methods.getBalance().call();
    //setEthBal(eth_amount);
    console.log(eth_amount);
  }
  const insertCoin = () => {
    contract_wallet.methods.sendViaCall().send({ 
      from: account,
      gas: 3000000,
      value: 100000000000000
    });
  }
  return (
    <>
    <a onClick={connectWallet}>Connect Wallet</a>
    {account}
    <button onClick={getBalance}>temp-339</button>
    <button onClick={insertCoin}>temp-001</button>
    </>
  );
}
