import logo from '../logo.svg';
import '../App.css';
import React from "react";
import { Link } from 'react-router-dom';

export class Home extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      user: this.props.user,
    };
    //this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  koujicyuu = () => {
    alert('공사중');
  }

  render(){
    return(
      <div className="App">
        <section >
          <p style={{textAlign: 'left', margin:'0px'}}>{sessionStorage.getItem('userid')}</p>
        </section>
        <section className="App-header">
          <nav className='hori'>
          <Link to="/search/idea" style={{ textDecoration: 'none' }}>
              <button className='button04'>아이디어 광장</button>
            </Link>
            <Link to="/search/market" style={{ textDecoration: 'none' }}>
              <button className='button04'>아이디어 시장</button>
            </Link>

          </nav> 
          <section>
            <p style={{fontSize: "15px"}}>아이디어 시장: 아이디어를 의뢰하거나 사고팔수 있습니다.</p>
            <p style={{fontSize: "15px"}}>아이디어 광장: 아이디어를 자유롭게 열람하고 공유할 수 있습니다.</p>
          </section>
      </section>
    </div>
    )
  }
}
