import React from "react";
import { useState, useEffect } from "react";
import { getIdeaOne, putFileReadFact } from "../api";

export const Vender = ({title, teamid, confidence}) => {
    const [metaMaskFlag, setMetaMaskFlag] = useState(false);
    const [account,setAccount] = useState(null);
	const [errorMessage, setErrorMessage] = useState(null);

	useEffect(() => {
		tmpFlag = window.ethereum && window.ethereum.isMetaMask;
		setMetaMaskFlag(tmpFlag);
	},[]);

    const connectWallet = () => {
		window.ethereum
		.request({ method: "eth_requestAccounts" })
		.then((result) => {
			  setAccount(result[0]);
		})
		.catch((error) => {
		  setErrorMessage(error.message);
		});
	}

  return (
    <a onClick={connectWallet}>Connect Wallet</a>
  );
}
