import React, { useEffect, useState } from "react";
import { postUserRegi } from "../api";

export const LoginInput = () => {

  const [recognize, setRecog] = useState(false);

  async function loginSubmit(evt) {
    const inputId = parseInt(evt.target.elements.id.value, 10);
    const inputPass = parseInt(evt.target.elements.pw.value, 10);

    const record = {
      playerid: inputId,
      userpass: inputPass
    };

  }
  return(
    <section className="App-header">
    <form onSubmit={loginSubmit}>
      login <br/>
      <input className='input-login' name="id" placeholder='id'/><br/>
      <input className='input-login' name="pw" placeholder='password'/><br/>
      <button type="submit" className="App-exeButton2">login</button>
    </form>
    <br/><br/>
    <a className="App-link" href="http://localhost:3000/useregist">아이디가 없으신가요?</a>
    </section>
  )
}

export const RegistInput = () => {
  async function loginSubmit(evt) {
    const record = {
      playerid: evt.target.elements.id.value,
      userpass: evt.target.elements.pw.value,
      username: evt.target.elements.name.value
    };
    await postUserRegi(record);
  }
  return(
    <section className="App-header">
      <form onSubmit={loginSubmit}>
        <input className='input-login' name="id" placeholder='id'/><br/>
        <input className='input-login' name="pw" placeholder='password'/><br/>
        <input className='input-login' name="name" placeholder='name'/><br/>
        <button type="submit" onClick={() => alert('계정 생성 완료')} className="App-exeButton2">submit</button>
      </form>
    </section>
  )
}
