import '../components/modal.css';
import React from 'react';
import { useState, useEffect } from "react";
import { ListItemsCompnt } from '../components/ItemsCpnt';
import {AlertCardCpnt} from '../components/ItemsCpnt';
import { JoinModal } from '../components/joinCpnt';
import { getHolds, getIdeaOne, getMyIdeas, getPlayersId, getPlayerInfo,
  getFileHash, putTimeOut
} from '../api.js';

export class UserOpt extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      ptcp: null,
      viewmode: 0,
      userTokn: 0,
      uid: sessionStorage.getItem('userid'),
    };
  }

  componentDidMount = () => {
    getPlayerInfo(this.state.uid).then(user => {
      console.log(user.token);
      this.setState({userTokn: user.token});
    })
  }

  adminTimeout = () => {
    putTimeOut().then(result => {
      console.log(result);
    })
  }

  setShowIdea = () => {
    const type = this.state.viewmode;
    if(type === 0){
      return(
        <>
        <p style={{margin: 15, fontSize: "19px"}}>
          my idea 버튼: 내가 등록한 아이디어를 화면에 표시합니다<br/>
        alert 버튼: 수신된 알림을 화면에 표시합니다.</p></>
      )
    }
    else if(type === 2){
      return(
        <Type_alert
          account = {this.state.uid}
        />
      )
    }
    else{
      return(
        <Type_myidea
          account = {this.state.uid}
        />
      )
    }
  }

  initPage = () => {
    return(
      <div className='App-header'>
        <p style={{textAlign: 'left', margin:'0px'}}>USER ID-{this.state.uid}</p>
        {this.state.uid === 'admin' ? <>
          <button onClick={() => {this.adminTimeout()}}>temp-322</button>
          <button onClick={() => {this.adminFileHash()}}>temp-904</button>
        </> : <></>}
        <button className='button04' style={{width:'300px', height:'100px', alignSelf:'center'}}>보유 포인트: { this.state.userTokn}</button>
        <section>
          <button onClick={() =>this.setState({viewmode: 1})}>my idea</button>
          <button onClick={() =>this.setState({viewmode: 2})}>alert</button>
        </section>
        <this.setShowIdea></this.setShowIdea>
      </div>
    )
  }
  render(){
    return(
      <section>
      {this.state.uid !== null ? <this.initPage></this.initPage> 
      : <p>내 정보를 보려면 우선 로그인해야 합니다.</p>}
      </section>
    )
  }
}

class Type_alert extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      alertList: [],
      alertName: null,
      cont: null,
      account: this.props.account
    };
  }
  componentDidMount = () => {
    this.setAlertList();
  }
  setAlertList = () => {
    let copyAlert = [];
    getPlayersId(this.state.account).then((playerId) => {
      getHolds(playerId).then((data) => {
        //console.log(data);
        const length = data.length;
        for(let iter = 0; iter < length; ++iter){
          copyAlert.push(data[iter]);
        }
        this.setState({alertList: copyAlert});
      })
    })
  }
  modalClose = () => {
    this.setState({showModal: false});
    this.setAlertList();
  }
  handleClick = (_searchItems) => {
    console.log('v ', _searchItems);
    this.setState({cont: _searchItems});
    this.setState({showModal: true});
  }

  render(){
    return(
      <>
        <p>알림 {this.state.alertList.length}건.</p>
        {this.state.alertList.map(searchItems => (
          <AlertCardCpnt
            key={searchItems.id}
            title = {searchItems.status}
            description = {'click to check'}
            onClick={() => this.handleClick(searchItems)}
          />
        ))}
        <JoinModal
          showFlag={this.state.showModal}
          content = {this.state.cont}
          account = {this.state.account}
          contract = {this.state.contract}
          onClick={()=>{this.modalClose()}}
        />
      </>
    )
  }
}
const Type_myidea = ({account}) => {
  const [teams, setTeams] = useState([]);

  useEffect(()=>{
    findMyIdeas();
  },[]);

  const findMyIdeas = () => {
    getMyIdeas(account).then(async(ideas) => {
      const result = await setMyIdea(ideas);
      setTeams(result);
      console.log(result);
    })
  }
  const setMyIdea = (_ideas) => {
    let tempArray = [];
    return new Promise(resolve => {
      for(let iter = 0; _ideas.length > iter; ++iter){
        getIdeaOne(_ideas[iter].teamId).then(team => {
          tempArray.push(team);
        })
      }
      resolve(tempArray);
    })
  }
  return(
    <div style={{alignSelf:'center'}}>
    {teams.map(searchItems => (
      <ListItemsCompnt
        key={searchItems.title}
        title = {searchItems.title}
        description = {searchItems.description}
        teamid = {searchItems.id}
        type = {searchItems.type}
        genre = {searchItems.jenre}
      />
    ))}
    </div>
  )
}
