import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import { ListItemsCompnt } from '../components/ItemsCpnt';
import { getMyIdeas, getIdeaOne, postCreateIdea } from "../api.js";

export const MarketIead = () => {
  const navigate = useNavigate();
  const account = useState(sessionStorage.getItem('userid'));
  const [firstPage, setFirst] = useState(false);
  const [secondPage, setSecond] = useState(false);
  const [thirdPage, setThird] = useState(false);

  const [ideaInstance, setInstance] = useState(null);
  const [title, setTitle] = useState();
  const [genre, setGenre] = useState('');
  const [genreCode, setCode] = useState();
  const [price, setPrice] = useState(0);

  useEffect(() => {
    if(account !== null)
        setFirst(true);
  },[]);

  const uploadBtn = () => {
    const result = window.confirm('아이디어를 업로드 하시겠습니까?');
    if(result){
      const record = {

      };
      postCreateIdea(record).then(result => {
        if(result){
          if(!alert('아이디어가 업로드되었습니다.'))
            navigate("/search/idea");
        }
        else
          alert('err40022');
      })
    }
  }
  const DoneSection = () => {
      return(
        <section style={{marginLeft:'3em', marginRight:'7em'}} className="App-display">
          <p>입력 내용을 다시한번 확인해주세요</p>
            아이디어 분야: {genre} <br/>
            경매 시초가: {price} <br/>
            아이디어 제목: {title} <br/>
            아이디어 설명: <br/>
          <br/><br/>
          <button onClick={uploadBtn} className="App-exeButton2" style={{alignSelf:'center'}}>confirm</button>
        </section>
      )
  }
  const temp = () => {
    console.log(ideaInstance);
  }

  return(
      <div className="App">
        <div className="App-header">
        <button onClick={temp}>009</button>
        {firstPage ? <>
          {secondPage ? <>

            </>:<FirstSection account={account[0]} setIdea={(e)=>setInstance(e)}/>}
          </> : <> 
            <p>아이디어 판매를 하려면 먼저 로그인해야 합니다.</p>
          </>}
        </div>
      </div>
    )
}
const FirstSection = ({account, setIdea}) => {
  const [teams, setTeams] = useState([]);
  const [selectOne, setSelected] = useState();

  useEffect(()=>{
    findMyIdeas();
  },[]);

  const findMyIdeas = () => {
    getMyIdeas(account).then(async(ideas) => {
      const result = await setMyIdea(ideas);
      setTimeout(() => {
        setTeams(result);
        //console.log(result.length);
      }, 439);
    })
  }
  const setMyIdea = (_ideas) => {
    let tempArray = [];
    return new Promise(resolve => {
      for(let iter = 0; _ideas.length > iter; ++iter){
        getIdeaOne(_ideas[iter].teamId).then(team => {
          tempArray.push(team);
        })
      }
      resolve(tempArray);
    })
  }
  const temp = () => {
    console.log(selectOne);
  }
  return(
    <div style={{alignSelf:'center'}}>
      <p>완료된 아이디어중 경매에 내놓을 아이디어를 선택해주십시오</p>
      <button onClick={temp}>test</button>
      {teams.map((searchItems, index) => (
        <ListItemsCompnt
          key={index}
          title = {searchItems.title}
          description = {searchItems.description}
          teamid = {searchItems.id}
          type = {searchItems.type}
          genre = {searchItems.jenre}
          display={true}
          choice = {(e)=>setIdea(e)}
        />
      ))}
    </div>
  )
}
