import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import { getMyIdeas, postCreateIdea } from "../api.js";

export const MarketIead = () => {
  const navigate = useNavigate();
  const account = useState(sessionStorage.getItem('userid'));
  const [firstPage, setFirst] = useState(false);
  const [secondPage, setSecond] = useState(false);
  const [thirdPage, setThird] = useState(false);

  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [fbolb, setBolb] = useState();
  const [fbolb2, setBolb2] = useState();
  const [file, setFile] = useState(false);
  const [genre, setGenre] = useState('');
  const [genreCode, setCode] = useState();
  const [price, setPrice] = useState(0);

  useEffect(() => {
    if(account !== null)
        setFirst(true);
  },[]);

  const uploadBtn = () => {
    const result = window.confirm('아이디어를 업로드 하시겠습니까?');
    if(result){
      const record = {
        name: title,
        desc: desc,
        useraddr: account,
        mode: 1,
        price: price,
        genre: genreCode,
        file: file,
        fbolb: fbolb,
        fbolb2: fbolb2
      };
      postCreateIdea(record).then(result => {
        if(result){
          if(!alert('아이디어가 업로드되었습니다.'))
            navigate("/search/idea");
        }
        else
          alert('err40022');
      })
    }
  }
  const DoneSection = () => {
      return(
        <section style={{marginLeft:'3em', marginRight:'7em'}} className="App-display">
          <p>입력 내용을 다시한번 확인해주세요</p>
            아이디어 분야: {genre} <br/>
            경매 시초가: {price} <br/>
            아이디어 제목: {title} <br/>
            아이디어 설명: <br/>
            <section style={{fontSize:'smaller', color:'darkgray'}}>{desc}</section>
          <br/><br/>
          <button onClick={uploadBtn} className="App-exeButton2" style={{alignSelf:'center'}}>confirm</button>
        </section>
      )
  }

  return(
      <div className="App">
        <div className="App-header">
        {firstPage ? <>
          {secondPage ? <>

            </>:<FirstSection account={account}/>}
          </> : <> 
            <p>아이디어 판매를 하려면 먼저 로그인해야 합니다.</p>
          </>}
        </div>
      </div>
    )
}
const FirstSection = ({account}) => {
  const [teams, setTeams] = useState([]);

  useEffect(()=>{
    findMyIdeas();
  },[]);

  const findMyIdeas = () => {
    getMyIdeas(account).then(async(ideas) => {
      setTeams(await setMyIdea(ideas));
    })
  }
  const setMyIdea = (_ideas) => {
    let tempArray = [];
    return new Promise(resolve => {
      for(let iter = 0; _ideas.length > iter; ++iter){
        getIdeaOne(_ideas[iter].teamId).then(team => {
          tempArray.push(team);
        })
      }
      resolve(tempArray);
    })
  }

  return(
    <div style={{alignSelf:'center'}}>
    {teams.map((searchItems, index) => (
      <ListItemsCompnt
        key={index}
        title = {searchItems.title}
        description = {searchItems.description}
        teamid = {searchItems.id}
        type = {searchItems.type}
        genre = {searchItems.jenre}
      />
    ))}
    </div>
  )
}
