import logo from '../logo.svg';
import '../App.css';
import React from "react";
import { Link } from 'react-router-dom';

export class Home extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      squareFontSize: '15px',
      auctionFontSize: '15px',
      coopFontSize: '15px'
    };
    //this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  componentDidMount = () => {
    if(this.state.uid !== null){
      this.setState({logined: true});
    }

  }
  koujicyuu = () => {
    alert('공사중');
  }

  storageClear = () => {
    sessionStorage.clear();
    window.location.reload();
  }

  render(){
    return(
      <div className="App">
        <section className="App-header">
          <section>
            <br/><br/><br/>
          </section>
          <button onClick={this.koujicyuu} className='button04' style={{alignSelf:"center", width:"430px", height:'130px'}}>쓰레드 웹의 총 이더량: 100 eth <br/>유통중인 총 포인트량: 250p</button>
          <nav className='hori'>
            {/* <button className='space'></button> */}
            <Link to="/search/idea" style={{ textDecoration: 'none' }}>
              <button className='button04' onMouseOut={()=>this.setState({squareFontSize: '15px'})} onMouseOver={() => this.setState({squareFontSize: '21px'})}>광장</button>
            </Link>
            <Link to="/search/market" style={{ textDecoration: 'none' }}>
              <button className='button04'onMouseOut={()=>this.setState({auctionFontSize: '15px'})} onMouseOver={() => this.setState({auctionFontSize: '21px'})}>시장</button>
            </Link>
            {/* <Link to="/search/market" style={{ textDecoration: 'none' }}> */}
              <button onClick={this.koujicyuu} className='button04' onMouseOut={()=>this.setState({coopFontSize: '15px'})} onMouseOver={() => this.setState({coopFontSize: '21px'})}>협력</button>
            {/* </Link> */}
          </nav>
          <section>
            <p style={{fontSize: this.state.squareFontSize }}>광장: 아이디어를 자유롭게 공유하고 열람할 수 있습니다.</p>
            <p style={{fontSize: this.state.auctionFontSize}}>시장: 아이디어 경매를 통해 아이디어를 사고팔수 있습니다.</p>
            <p style={{fontSize: this.state.coopFontSize}}>협력: 아이디어를 타인과 협력하여 완성해나갈 수 있습니다.</p>
          </section>
      </section>
    </div>
    )
  }
}
