import React from 'react';
import { AuctionInput } from '../components/auctionInput.js';
import { withRouter } from '../components/withRouteCpnt.js';
import { ViewFile } from '../components/fileViewer.js';
import { PushInput } from '../components/apushInput.js';
import { StatusGuide } from '../components/statusGuide.js';
import { 
    getIdeaOwner, getPlayers, putAddGood,
    getIdeaOne, getRemainTime, postTeamReply, getReply, getIdeaPoints } from '../api';

class IdeaDetails extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      slide: true,
      remain: null,
      uid: sessionStorage.getItem('userid'),
      owner: null,
      showFlag: false,
      ptcpsNum: 0,
      contents: null,
      ideaStatus: null,
    };
  }
  componentWillUnmount = () => {
    clearTimeout(this.timer);
  }
  componentDidMount = () => {
  //console.log(this.props.router);
  this.getContents();

    setTimeout(() => {
      this.setState({slide: false});
    }, 1300);
  }
  getContents = async() => {
    const teamId = parseInt(this.props.router.params.teamid, 10);
    const contents = await getIdeaOne(teamId);
    this.setState({contents: contents});
    this.getPlayer(contents.id);
    this.getStatus();
  }
  getPlayer = (_ideaid) => {
    getIdeaOwner(_ideaid).then((data) => {
      this.setState({owner: data});
    });
    getPlayers(_ideaid).then((data => {
      this.setState({ptcpsNum: data.length});
    }));
  }
  getStatus = () => {
    console.log(this.state.contents)
    switch(this.state.contents.progress){
      case 0:
        this.setState({ideaStatus: 'open'});
        break;
      case 1:
        this.setState({ideaStatus: 'excute'});
        break;
      case 2:
        this.setState({ideaStatus: 'vote'});
        break;
    }
  }

  viewBranch = () => {
