import React from "react";
import { useState, useEffect } from "react";
import FileViewer from "react-file-viewer";
import { getIdeaOne, putFileReadFact } from "../api";

export const ViewFile = ({title, teamid, confidence}) => {
  const [checkFild, setCheck] = useState(false);
  const [view, setView] = useState(false);
  const file = `https://ideaideaserver.herokuapp.com/viewdocx/${title}`;
  const fileG = `https://docs.google.com/viewer?url=https://ideaideaserver.herokuapp.com/viewdocx/${title}&embedded=true`;
  //const file = `http://localhost:3039/viewdocx/${title}`;
  const type = "pdf";

  useEffect(()=>{
    if(confidence)
      setFileRead();
    fileinspect();
  },[]);

  const setFileRead = () => {
    const record = {
      teamId: teamid,
      userId: sessionStorage.getItem('userid'),
      docuName: title
    }
    putFileReadFact(record).then(result => {
      console.log(result);
    })
  }
  const fileinspect = async() => {
    const fileCheck = await getIdeaOne(teamid);
    console.log(fileCheck.file);
    setCheck(fileCheck.file);
  }
  const handleView = () => {
    if(checkFild)
      setView(!view);
    else
      alert('file does not exist');
  };

  const onClickUrl = (_url) => {
    return () => openInNewTab(_url);
  }
  const openInNewTab = (url) => {
    const newWindow = window.open(url, '_blank', 'noopener,noreferrer')
    if (newWindow) newWindow.opener = null
  }
  return (
    <div style={{color:'black'}}>
      { confidence ? <button onClick={onClickUrl(fileG)} className="button02">아이디어 핵심파일 열람</button>
        : <button onClick={onClickUrl(fileG)} className="button02">이 아이디어의 상세설명 열기</button>}
    </div>
  );
}
