    return(
      <>
        <p>100자 정도의 아이디어 설명을 입력해주세요</p>
        <button onClick={temp}>next</button>
      </>
    )
  }

  const temp = () => {
      console.log(title);
  }

  return(
      <div className="App">
        <div className="App-header">
        {firstPage ? <>
          {secondPage ? <>
            {thirdPage ? <>
              {forthPage ? <>
                </>:<ThirdSection/>}
              </>:<SecondSection/>}
            </>:<FirstSection/>}
          </> : <> 
            <p>아이디어 공유를 하려면 먼저 로그인해야 합니다.</p>
          </>}
