import React, { useState, useEffect } from "react";
import { postCreateIdea } from "../api.js";

export const MarketIead = () => {

  const [account, setAccount] = useState(sessionStorage.getItem('userid'));
  const [firstPage, setFirst] = useState(false);
  const [secondPage, setSecond] = useState(false);
  const [thirdPage, setThird] = useState(false);
  const [fourthPage, setFourth] = useState(false);
  const [fifthPage, setFifth] = useState(false);
  const [sixthPage, setSixth] = useState(false);
  const [seventhPage, setSeventh] = useState(false);

  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [fbolb, setBolb] = useState();
  const [fbolb2, setBolb2] = useState();
  const [file, setFile] = useState(false);
  const [file2, setFile2] = useState(false);
  const [genre, setGenre] = useState('');
  const [genreCode, setCode] = useState();
  const [price, setPrice] = useState(0);

  useEffect(() => {
    if(account !== null)
        setFirst(true);
  },[]);

  const checked = (evt) => {
    const category = evt.target.value;
    switch(category){
      case '0':
        setGenre('공학/과학');
        setCode(0);
        break;
      case '1':
        setGenre('사회/문화');
        setCode(1);
        break;
      case '2':
        setGenre('음악/미술');
        setCode(2);
        break;
      case '3':
        setGenre('기타분야');
        setCode(3);
        break;
    }
    setSecond(true);
  }
  const FirstSection = () => {
    return(
      <>
        <p>판매하려는 아이디어의 분류가 어디에 속하나요?</p>
        <section>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='0' onChange={checked}></input>
            공학/과학</label>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='1' onChange={checked}></input>
            사회/문화</label>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='2' onChange={checked}></input>
            음악/미술</label>
          <label style={{cursor: 'pointer'}}><input type="checkbox" value='3' onChange={checked}></input>
            기타분야</label>
        </section>
      </>
    )
  }
  const uploadBtn = () => {
    const result = window.confirm('아이디어를 업로드 하시겠습니까?');
    if(result){
      const record = {
        name: title,
        desc: desc,
        useraddr: account,
        mode: 1,
        price: price,
        genre: genreCode,
        file: file,
        fbolb: fbolb,
        fbolb2: fbolb2
      };
      postCreateIdea(record).then(result => {
        if(result)
          alert('아이디어가 업로드되었습니다.');
        else
          alert('err40022');
      })
    }
  }
  const onFileInputChange = (_whichFile) => (e) => {
    const reader = new FileReader();
    const fileByteArray = [];

    reader.readAsArrayBuffer(e.target.files[0]);
    reader.onloadend = (_evt) => {
      if (_evt.target.readyState === FileReader.DONE) {
        const arrayBuffer = _evt.target.result,
        array = new Uint8Array(arrayBuffer);
        for (const a of array) {
          fileByteArray.push(a);
        }
        console.log(fileByteArray);
        if(_whichFile){
          setFile(true);
          setBolb(fileByteArray);
        }
        else{
          setFile2(true);
          setBolb2(fileByteArray);
        }
      }
    }
  }
  const fileCheck = () => {
    if(file === true && file2 === true)
      setFifth(true);
  }

  const FifthSection = () => {
    return(
      <>
        <p>아이디어에 대한 설명 파일을 올려주세요<br/>사람들이 이 파일내용을 보고 경매참여를 결정지을 수 있도록 거래하고자 하는 아이디어의 전반적인 내용을 서술해주세요.</p>
        <section style={{alignSelf:'center'}}>
        <label className="inputfile"><input type='file' onChange={onFileInputChange(false)}/>파일선택</label><br/><br/><br/>
        <p>아이디어의 구현방법, 원리등 모든 내용을 담은 파일을 올려주세요<br/>이 파일은 경매종료 후 가장 높은 호가를 부른 사람에게만 보여지며 파일 도용/부인 방지를 위해 열람사실은 블록체인에 영구히 보관됩니다.</p>
        <label className="inputfile"><input type='file' onChange={onFileInputChange(true)}/>파일선택</label>
        <br/><br/><br/><button onClick={fileCheck}>next</button>
        </section>
      </>
    )
  }
  const DoneSection = () => {
      return(
        <section style={{marginLeft:'3em', marginRight:'7em'}} className="App-display">
          <p>입력 내용을 다시한번 확인해주세요</p>
            아이디어 분야: {genre} <br/>
            경매 시초가: {price} <br/>
            아이디어 제목: {title} <br/>
            아이디어 설명: <br/>
            <section style={{fontSize:'smaller', color:'darkgray'}}>{desc}</section>
          <br/><br/>
          <button onClick={uploadBtn} className="App-exeButton2" style={{alignSelf:'center'}}>confirm</button>
        </section>
      )
  }

  return(
      <div className="App">
        <div className="App-header">
        {firstPage ? <>
          {secondPage ? <>
            {thirdPage ? <>
              {fourthPage ? <>
                {fifthPage ? <>
                  {sixthPage ? <>
                    {seventhPage ? <>
                      </>:<DoneSection />}
                    </>:<SixthSection setPrice={setPrice} setSixth={setSixth} />}
                  </>:<FifthSection />}
                </>:<ThirdSection setDesc={setDesc} setFourth={setFourth}/>}
              </>:<SecondSection setTitle={setTitle} setThird={setThird}/>}
            </>:<FirstSection />}
          </> : <> 
            <p>아이디어 공유를 하려면 먼저 로그인해야 합니다.</p>
          </>}
        </div>
      </div>
    )
}

const SixthSection = ({setPrice, setSixth}) => {
  return(
    <section style={{alignSelf:'center'}}>
      <p>경매 시작가를 정해주세요<br/>경매 최초 호가는 해당가격이 되며 경매 종료시점은 첫 입찰자가 참가한 후부터 3일 입니다.</p>
      <input type='number' min={1} max={999999} step={1} onChange={(evt)=>setPrice(evt.target.value)}></input><br/><br/>
      <button onClick={() => setSixth(true)}>next</button>
    </section>
  )
}
const ThirdSection = ({setDesc, setFourth}) => {
    return(
        <>
          <p>100자 정도의 아이디어 설명을 입력해주세요 <br/>이 설명을 잘 쓸수록 클릭수에 큰 영향을 줍니다.</p>
          <section style={{alignSelf:'center'}}>
          <textarea name="docudesc" rows='5' cols='55' placeholder="100자 이내의 아이디어 요약&#13;이 설명란을 다 채울 시 107자가 쓰여질 수 있습니다..&#13;되도록 줄바꿈없이 기입해주세요" 
            onChange={(evt) => setDesc(evt.target.value)}/><br/>
          <button onClick={() => setFourth(true)}>next</button>
          </section>
        </>
      )
}

const SecondSection = ({setTitle, setThird}) => {
  return(
    <>
      <p>아이디어 제목을 입력해주세요</p>
      <section style={{alignSelf:'center'}}>
      <input id="text2" type="text" onChange={(evt)=> setTitle(evt.target.value)} placeholder="아이디어 제목"></input>
      <button onClick={() => setThird(true)}>next</button>
      </section>
    </>
  )
}
