import React from "react";
import { postCreateIdea } from "../api.js";

export class UploadIead extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      price: null,
      title: null,
      desc: null,
      fblob: null,
      uploadMode: null,
      fundidea: false,
      modeCheck: false,
      account: sessionStorage.getItem('userid')
    };
    //this.modalClose = this.modalClose.bind(this);
  }

  componentDidMount = () => {
    console.log(this.state.account);
  }

  handleFormSubmit = async(event) => {
    const result = window.confirm('업로드한 아이디어는 모두에게 공개됩니다.\r\n업로드시 10포인트가 지급됩니다.\r\n아이디어를 업로드 하시겠습니까?');
    event.preventDefault();
    if(result){
      const record = {
        name: event.target.elements.docuname.value,
        desc: event.target.elements.docudesc.value,
        useraddr: this.state.account,
        price: event.target.elements.price.value,
        goaldate: event.target.elements.days.value,
        mode: this.state.uploadMode,
        fbolb: this.state.fblob
      };
      console.log(result);
      // const result = await postCreateIdea(record);
      // if(result)
      //   alert('아이디어가 업로드되었습니다.');
      // else
      //   alert('err40022');
    }
  }

  onFileInputChange = (e) => {
    const reader = new FileReader();
    const fileByteArray = [];
    //console.log(evt.target.files[0]);

    reader.readAsArrayBuffer(e.target.files[0]);
    reader.onloadend = (_evt) => {
      if (_evt.target.readyState === FileReader.DONE) {
        const arrayBuffer = _evt.target.result,
        array = new Uint8Array(arrayBuffer);
        for (const a of array) {
          fileByteArray.push(a);
        }
        console.log(fileByteArray);
        this.setState({fblob: fileByteArray});
      }
    }
  }

  inputForm = () => {
    return(<>
      <form onSubmit={this.handleFormSubmit} style={{marginLeft:'5em'}}>
        <br/>
        아이디어 제목<br/>
        <input name="docuname" size='38' placeholder='제목의 중요성은 열번 말해도 지나치지 않습니다' defaultValue={this.state.title} onChange={(evt) => this.setState({title: evt.target.value})}/><br/>
        아이디어 초록<br/>
        <textarea name="docudesc" rows='5' cols='42' placeholder="1~3줄 정도의 아이디어 요약&#13;&#13;
        " defaultValue={this.state.desc} onChange={(evt) => this.setState({desc: evt.target.value})}/>
        <br/>
        스토리텔링 파일<br/>
        <p style={{color: '#C6C6C6', fontSize: "16px", margin: 0}}>초록을 보고 들어온 사람들이 더 자세한 정보를 얻을 수 있도록 스토리텔링 파일을 업로드 해주세요</p>
        <input type='file' onChange={this.onFileInputChange}/>

        {this.state.fundidea ? <>
          <br/><br/>
          프로젝트 기간<br/>
          <p style={{color: '#C6C6C6', fontSize: "16px", margin: 0}}>후원된 포인트가 목표치에 도달한 시점부터 프로젝트가 완료되는 시점까지의 기간을 설정해주세요</p>
          <input type="number" id="days" name="days" min="3" max="365" defaultValue={null}/> 일
          <br/><br/>
          목표 포인트:<br/>
          <p style={{color: '#C6C6C6', fontSize: "16px", margin: 0}}>후원된 포인트가 목표치에 도달하면 프로젝트가 시작됩니다</p>
          <input type="number" id="days" name="price" min="1" max="999" defaultValue={0}/> 포인트
        </> : <> 
          <br/>
        </>}
        <br/><br/>
        <button className="App-exeButton2" type="submit">
        작성완료
        </button>
      </form>
    </>)
  }

  checkRadio = (evt) => {
    console.log(this.state.uploadMode);
    switch(evt.target.value){
      case 'dirc':
        this.setState({uploadMode: 0});
        this.setState({fundidea: true});
        break;
      case 'cycl':
        this.setState({uploadMode: 2});
        break;
      case 'fund':
        this.setState({uploadMode: 0});
    }
    this.setState({modeCheck: true});
  }

  render(){
    return(
      <div className="App">
        <div className="App-header">
          <section className="App-display">
            {this.state.modeCheck ? <this.inputForm></this.inputForm> : <>
            <p>아이디어 업로드 방식을 정해주세요</p>
            <br/>
            <label style={{cursor: 'pointer'}}><input type="radio" name="color1" value="dirc" onChange={this.checkRadio}/> 프로젝트형</label>
            <ul style={{color: '#C6C6C6', fontSize: "18px", marginRight: '8em'}}><li>후원을 받아 프로젝트를 진행합니다. </li>
            <li>아이디어 실현에 필요한 목표 포인트를 설정하고 후원을 받아 아이디어를 완성시키세요</li>
            <li>후원된 포인트는 프로젝트 진행이 성공적으로 완료된 후 회수할 수 있습니다</li>
            </ul><br/>
            <label style={{cursor: 'pointer'}}><input type="radio" name="color1" value="fund" onChange={this.checkRadio}/> 아이디어 제안형 </label>
